@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("blaulichtreport-saarland.de") {
  body > center {display: none !important}
  body > strong {display: none !important}
  body > b      {display: none !important}
  body > i      {display: none !important}
  body > font   {display: none !important}
  body > span   {display: none !important}
  body > div:nth-of-type(2)      {display: none !important}
  body > [id*="0"] {display: none !important}
  body > [id*="1"] {display: none !important}
  body > [id*="2"] {display: none !important}
  body > [id*="3"] {display: none !important}
  body > [id*="4"] {display: none !important}
  body > [id*="5"] {display: none !important}
  body > [id*="6"] {display: none !important}
  body > [id*="7"] {display: none !important}
  body > [id*="8"] {display: none !important}
  body > [id*="9"] {display: none !important}
}
