css:
@-moz-document domain(en.wikipedia.org), domain(de.wikipedia.org), domain(fr.wikipedia.org) {
p sup.reference a { font-weight:bold; font-size:140% !important; color:#55C !important; background:#FFF !important; }
p:hover sup.reference a { background:#FFFF00 !important; color:#1010FF !important; }
p:hover sup.reference a:visited { color: #5A3696 !important; }
p:hover sup.reference a:active { color: #FAA700 !important; background:#0808F8 !important; }
}
