css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://blog.livedoor.jp/itsoku/") {
   
  #top_ad,
  #ad div,/* 広告 */  
  .plugin-ad,/* 広告 */
  #lite_link,/* 広告 */
  .blog_ad2,/* 広告 */
  .ad2,/* 広告 */
  #article_mid,/* 記事内広告 */

  .plugin-blogroll,
  .footer-ranking,/* カテゴリランキング */
  .footer-nav,/* フッターナビゲーション */
  .footer-bottom,/* フッター */
  .footerPcLink,/* フッター表示切替 */
  .article-button-simple,/* SNS */
  .amazlet-box,/* Amazon */
  .article-comment-count,/* コメント数 */
  
  [id^="imobile"],/* 広告 */
  [id^="comments"],/* コメント欄 */
  
  .content > aside:last-of-type,/* FB */
  .container-inner > aside:last-of-type,/* FB */
  .content > nav/* SNS */
  {display: none!important;}

    /* 画像リンク */
  #article-contents img {
    pointer-events: none!important;
    -moz-user-select: none!important;
  }
  
}
