css:
@-moz-document domain("vimeo.com") {
  .user_portraits,
  .user_stats .bubble_list.nipple_left > .list_item:first-child::before,
  .user_stats .bubble_list.nipple_left > li:first-child::before,
  .activity_item .portrait_md,
  #info #page_header .portrait,
  .portrait_lg,
  .portrait_xlg,
  .portrait_sm,
  .topnav_menu_avatar img,
  .conversation_row td.user_cell,
  #comments article .portrait_wrapper {
    display: none !important;
  }
  .col_small .block .who_what {
    height: auto !important;
  }
  .col_small .block .who_what .portrait {
    visibility: hidden;
  }
  .topnav_menu_avatar > .topnav_has_dropdown {
    cursor: pointer;
    text-align: center;
    width: 32px;
  }
  .topnav_menu_avatar > .topnav_has_dropdown::after {
    content: "";
  }
  .topnav_menu_avatar > .topnav_has_dropdown::after {
    display: inline-block;
    font-family: VimeoTopnav;
    font-style: normal;
    font-variant: normal;
    text-transform: none;
    line-height: 1;
    text-decoration: inherit;
    text-rendering: optimizelegibility;
    -moz-osx-font-smoothing: grayscale;
    vertical-align: middle;
  }
  .activity_item.like::before {
    left: 20px !important;
  }
  .activity_item {
    padding-left: 96px !important;
  }
}
