css:
@-moz-document domain("trello.com") {

  /* userstyle: https://userstyles.org/styles/118209/trello-add-label-text-no-shadow
  */
.list-card-labels {
  display: flex !important;
}
.mod-card-front .label-text {
    display: inline !important;
}
.list-card-labels .card-label { 
  height:initial !important;
  width:initial !important;
  line-height:initial !important;
  padding: 0px 4px !important;
  font-size: 10px !important;
  margin-right: 2px !important;
  min-height:4px;
  min-width:45px;
  text-shadow: none; /* no text shadow */
  border-radius: 0 !important; /* no rounded corners */
}
.card-label.mod-card-front {
    max-width: initial !important; /* expand width if necessary */
    font-weight: 400 !important; /* no bold */
    text-transform: uppercase !important;
}

}
