css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mydramalist.com") {
  html {
    background-color : #222 !important;
  }
  body {

    /* Speed first */
    text-rendering             : optimizeSpeed;
    image-rendering            : optimizeSpeed;
    -webkit-image-rendering    : optimizeSpeed;

    text-shadow            : 0 0 0 #000;
    -webkit-font-smoothing : antialiased;

    -webkit-filter         : invert(1) hue-rotate(180deg);
    -ms-filter             : invert(1) hue-rotate(180deg);
    filter                 : invert(1) hue-rotate(180deg);
  }



  img, video, iframe, canvas, svg,
  embed[type='application/x-shockwave-flash'],
  object[type='application/x-shockwave-flash'],
  *[style*='url('] {
    -webkit-filter : invert(1) hue-rotate(180deg) !important;
    -ms-filter     : invert(1) hue-rotate(180deg) !important;
    filter         : invert(1) hue-rotate(180deg) !important;

  }
  
.discussions_trending { 
    -webkit-filter : invert(1) grayscale(1) brightness(120%) !important;
    -ms-filter     : invert(1) grayscale(1) brightness(120%) !important;
    filter         : invert(1) grayscale(1) brightness(120%) !important;
}
  
  #page-wmenu .page-item > a,
  #dramalist .table th > a,
  h2, .profile > li > a,
  .show-rating{text-shadow: unset !important}
  
}
