@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mangahere.co") {

.mangaread_top, .readpage_footer, .footer_nav, .footer_chapters, .footer_links, .footer_copy, iframe, .advimg72890 {display:none;}
.readpage_top {
width: 880px;
background-color: #000;
margin: 0 auto;
}
	.clearfix, .mb5, .mb10, .mb20, .m10316, .manga_ranking, .page_main {
		background: #333333 !important;
		color: #666 !important;
	}
	.header_top , body, .menu, .search_top, ul, .search_text {
		background: #222222 !important;
		background-color: #222222;
		color: #666 !important;
	}
	#back_top {
		background: #91999E !important;
		border-color: #222222 #222222 #222222 -moz-use-text-color;
		color: #666 !important;
	 }
	.directory_footer {
		background: #555555 !important;
		border-color: #222222 #222222 #222222 -moz-use-text-color;
		color: #666 !important;
	 }
}
