css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("touch.pixiv.net") {

  [class*="premium"],
  [class^="pixiv_game"],
  a[href^="http://touch.pixiv.net/premium"],
  a[href^="https://play.google.com/store"],
  a[href^="//d.pixiv.org/click?ad_group"],
  .hide-ads,
  #ad-header,
  #ad-footer,
  #ad-pager,
  #ad-comment-tag,
  #adstir_float,
  .ad-body,
  .ad-container,
  .ad-banner,
  .ad-topmost-header,
  .grid_ad,
  .ads-advs-ntvads,
  .comic-banner,
  .popular-search-pr,
  .platform
  {display: none!important;}
  
  .img-box .imgbox,
  .ugoira-player {
    width: 100%!important;
    height: auto!important;
  }
  
  .stream-item img  {
    max-width: 300px!important;
    max-height: 260px!important;
  }

}
