css:
@-moz-document domain("demo.phpmyadmin.net") {
html {
	-webkit-filter: invert(100%) !important;
}
img,
iframe,
frame {
	-webkit-filter: invert(100%) !important;
}
body {
	background: #ddd;
}
.sql, code.sql, .sqlvalidate {
   	-webkit-filter: invert(100%);
    background: #000;
}
#pmalogo {
	display: none;   
}
div#leftframelinks {
 	margin: 0px 10px 0px 10px; 
}
#serverinfo,
div.tools, 
.tblFooters{
	background: #ddd;
    text-shadow: 0 0 0 #ddd;
}
.odd:hover,
.even:hover,
.hover {
	background: #ccc !important;
    background-color: #ccc !important;
}
a,
a:visited,
.tblHeaders a:link, 
.tblHeaders a:active, 
.tblHeaders a:visited, 
div.tools a:link, 
div.tools a:visited, 
div.tools a:active, 
.tblFooters a:link, 
.tblFooters a:active, 
.tblFooters a:visited {
	color: #957373 !important;
}
a:hover {
	color: #957373 !important;
}
div.success,
h1.success {
    background-color: #8E69EE !important;
    border-color: #4E29CE !important;
    border-radius: 0px !important;
}
div.error,
h1.error {
    background-color: #55BBAF !important;
    border-color: #009595 !important;
    border-radius: 0px !important;    
}
}
