css:
@-moz-document domain("anilibria.tv") {
/************************************************
*Автор:      PersonV                            *
*Версия:     Anilibria.tv - Dark theme v0.4     *
*URL:        https://userstyles.org/users/289422*
*@Все права защищены! :)                        *
************************************************/
/*Удаляем мусор*/
#hslider/*Слайдер*/,#content > article > a /*Fast Anime*/, #content > article > div.torrent_content > center/*Реклама под плеером*/,#footer_center /*Реклама в footer*/,#s4et4iki /*Счетчики*/
{display:none!important;}
/*Фикс шапки*/
.block_fix 
{clear: both;height: 50px;
}
/*Смена цвета шапки*/
body > header, #headercontent, #topmenu > A
{background: #342e35!important;}
/*Смена цвета ника в шапке*/
#profile_block > form > div.userinfo > p:nth-child(1)
{  color: #626262!important;}
/*Смена фона сайта*/
html, body
{background: #383838 !important;}
/*Фикс меню*/
/*Меняет цвет рамки в меню на демный*/
nav#topmenu a, nav#topmenu a
{
    border-style: solid;
    border-left-width: 0;
    border-right-width: 0;
    border-top-width: 4px;
    border-bottom-width: 4px;
    border-color: #342e35;
}
#topmenu a {
    color: #a3a3a3;
}
nav#topmenu a, nav#topmenu a:visited {
    border-style: solid;
    border-left-width: 0;
    border-right-width: 0;
    border-top-width: 4px;
    border-bottom-width: 4px;
    border-color: #342e35;
}
nav#topmenu a.selected {border-top: 4px solid #d52c43;}
}

@-moz-document url-prefix("http://anilibria.tv/search/"), url-prefix("http://www.anilibria.tv/search/"), url-prefix("http://www.anilibria.tv/communication/forum/") {
/*Добавляет белую подложку*/
#content > article
{background: #FFF !important;}
}

@-moz-document url-prefix("http://anilibria.tv/communication/forum/"), url-prefix("http://www.anilibria.tv/communication/forum/") {
/*Подложка для пути к текущему топику форума*/
#content > article > div.forum-breadcrumb.forum-breadcrumb-top
{background: #FFF !important;}
}
