css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamedaradara.doorblog.jp") {

  .rss-margin,
  #link_body,
  [id^="recent_comments"],
  .article-social-btn-top,
  .article-social-btn-separate,
  #middle3_rss,
  .article-body-inner b,
  #middle_rss,
  .article-footer,
  #comments,
  #sns-kijishita,
  #trackbacks,
  .article-body-inner > br:nth-of-type(n+2):nth-of-type(-n+4),
  .article-body-inner > br:nth-last-of-type(-n+4),
  .left-column-inner > div:nth-last-of-type(4)
  {display: none!important;}
  
  #blog-footer 
  {height: auto!important;}
  .pv
  {visibility: hidden!important;}
  [id^="ad"],
  ins
  {display: none!important;}
  
}
