css:
@-moz-document url("https://signin.aws.amazon.com/oauth") {

/* Hide authentication error message */
#main_error
    {display: none  !important;}

/* Hide MFA checkbox */
#input_mfa_checkbox
    {display: none  !important;}
#mfa_checkbox
    {display: none  !important;}

/* Always display MFA Code input field */
#input_mfacode
	{display: block	!important;}

}
