css:
@-moz-document domain(cohones.mmarocks.pl) {

.messageList .message:nth-child(odd), .messageList .message:nth-child(even) { 
	border:none;
    box-shadow: 1px 1px 6px 0 rgba(95, 95, 95, 0.3);
	border-radius: 4px;
	background: #F5F5F5;
	padding: 16px 11px 10px 8px;
}
    
.messageList .message:nth-child(even) {
	background: #fdfdfd;
}
    
.primaryContent {
	background: #F5F5F5;
}

.messageList .message:nth-child(even) .primaryContent {
	background: #fdfdfd;
}

.message .messageContent {
	min-height:initial;
}

.messageText {
    font-family: tahoma;
    font-size: 13px;
	line-height: 22px;
}

.message .messageMeta { 
	border-top: 1px solid #ddd;
    margin-top: 20px;
    opacity: 0.4;
}
        
.message .secondaryContent {
    opacity: 0.4;
}

body, #content .pageContent { background: #eaeaea}

.messageUserBlock .extraUserInfo { display:none }

.messageUserBlock { border:none; padding-bottom:0; }

.messageUserBlock h3.userText { border:none; padding: 5px 0 0 0; }

.messageUserBlock .arrow { display:none }


.quickReply {
	background:none;    
}

.discussionListItem:hover, .discussionListItem:hover .posterAvatar, .discussionListItem:hover .stats {
	background:#f5f5f5;
}

.avatar img {
	max-width: 60px;
	max-height: 60px;
}

.messageUserInfo {
	width: 85px;
}

.message .messageInfo {
	margin-left:100px;
}


.messageUserBlock div.avatarHolder {
	padding:0
}
.messageUserBlock div.avatarHolder .onlineMarker {
	top:-1px;
}

}
