css:
 @-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/dashboard"), url-prefix("http://www.tumblr.com/reblog"), url-prefix("https://www.tumblr.com/reblog"), url-prefix("http://www.tumblr.com/edit"), url-prefix("https://www.tumblr.com/edit"), url-prefix("http://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/blog") {
  .original-reblog-content .reblog-header, .reblog-list-item--text-post-original .reblog-header, .reblog-list-title .reblog-header, .reblog-header .reblog-avatar {display: none !important; visibility: hidden !important;}
  .reblog-list, .post_container .reblog-list-item, .reblog-list-item.contributed-content {border: none !important}
  .post_container {padding-bottom: 5px !important}
  .reblog-list .reblog-list-item:nth-of-type(even) {background-color: rgba(231,231,231,0.5) !important;}
  .post_container .contributed-content {background-color: rgba(200,200,200,0.6) !important;}
  .post_container .reblog-content figure.tmblr-full {text-align: center !important}
  .post_container .reblog-content {overflow-x: hidden !important}
  .post_container .reblog-content, .post_container .reblog-title {overflow-y: hidden !important; line-height: 1.25em !important}
  .post_container .reblog-content figure.tmblr-full img {display: inline-block !important; min-width: auto !important; max-width: 100% !important}
  .post_tags {margin-top: 5px !important;} 
  .reblog-header {padding-left: 0 !important;}
  .post_container .reblog-list-item:last-of-type {padding-bottom: 15px !important;}
}
