css:
@-moz-document url-prefix(https://eschoolplus24.k12.ar.us/TAC24) {
td {
    font-family: Century Gothic !important;
    word-wrap: break-word;
}
th {
    vertical-align: middle;
}
.MenuSubHeader {
    color: white;
    font-size: 150%;
    padding: 10px;
}
#tblNews {
    padding: 10px 10px 0;
}
.CourseFilters {
    padding: 10px;
}
#tblMessageList,
#tblFileList {
    padding-top: 10px;
}
.BannerTitle {
    color: orange !important;
    font-size: 225% !important;
}
.HomeSection .readOnly {
    display: none !important;
}
#tdMyClasses {
    width: 900px !important;
}
#gvClassList {
    text-align: center;
}
#gvClassList tr td:nth-child(3),
#gvClassList tr th:nth-child(3) {
    display: none;
}
#gvClassList tr td:nth-child(4),
#gvClassList tr th:nth-child(4) {
    display: none;
}
#gvClassList tr td:nth-child(5),
#gvClassList tr th:nth-child(5) {
    display: none;
}
#gvClassList tr td:nth-child(6),
#gvClassList tr th:nth-child(6) {
    display: none;
}
#gvClassList tr td:nth-child(9),
#gvClassList tr th:nth-child(9) {
    display: none;
}
#gvClassList tr td:nth-child(14),
#gvClassList tr th:nth-child(14) {
    display: none;
}
#gvClassList tr td:nth-child(15),
#gvClassList tr th:nth-child(15) {
    display: none;
}
#tblMessageList tr td:nth-child(2),
#tblMessageList tr th:nth-child(2) {
    display: none;
}
}
