css:
@-moz-document domain("outlook.office365.com"), domain("portal.office.com") {
/* fix broken alternate/selected background */
.ms-bg-color-themeLight
{
    background-color: #dedede !important;
}

/* fix accent colors. replace red with a color of choice */
.ms-font-color-themePrimary
{
    color: red !important;
}

.ms-font-color-themePrimary-before:before, .ms-fcl-tp-b:before
{
    color: red !important;
}

.ms-border-color-themePrimary-before:before, .ms-bcl-tp-b:before 
{
    border-color: red !important;
}

.ms-bg-color-themeSecondary
{
    background-color: red !important;
}

.ms-font-color-themeSecondary-before:before, .ms-fcl-ts-b:before
{
    color: red !important;
}

.ms-bgc-tp
{
    color: red !important;
    background-color:transparent !important;
}

.ms-bgc-tp:hover
{
    color: black !important;
    background-color:red !important;
}

.ms-fcl-tp
{
    color: red !important;
}

.o365-theme-base a
{
    color: red !important;
}

.ms-font-color-themeDark
{
    color: red !important;
}

.ms-bg-color-themePrimary
{
    background-color: darkred !important;
    color: white;
}
.o365cs-base.o365cst.o365cs-topnavBGColor-2
{
    background-color: transparent !important;
}
.ms-bgc-tdr-h:hover
{
    background-color: red !important;
}

.o365cs-nav-tenantLogo
{
    display:none !important;
}
.o365cs-base.o365cst .o365cs-topnavLinkBackground-2
{
    background-color: transparent !important;
}

.o365cs-topnavText
{
    color: white;
}

.o365cs-topnavText:hover
{
    color: black;
}
}
