css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.downloadsquad.com/") {

html {
  width: 960px !important;
  margin: 0 auto !important;
  padding: 0 !important;
}

#slice, #topleader-wrap, #outerslice, .cobrand, #hat,
#subcontent, body .sandwich div, #grid2, #legal {
  display: none !important;
}

form, .searchform {
  margin: 0 !important;
}

#promo-area {
  width: 982px !important;
  margin: 0 auto !important;
}

#promo-area ul {
  margin: 0 auto !important;
  width: 980px !important;
}

.postbody, p > .byline, .posttags, .postmeta {
  width: 930px !important;
  margin: 0 auto !important;
}

h2 {
  text-align: center !important;
  width: 980px !important;
}

.filed-under {
  width: 960px !important;
  text-align: center !important;
}

}
