css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.yomiuri.co.jp") {
  #rightColumn {
    display: inline;
    float: right;
    margin: 4px 0 0;
    overflow: hidden;
    padding: 0 0 5px;
    width: 0;
}
    #mainContentsInr {
    margin: 0px 0px 0px 0px;
}
.row-contents {
    min-width: 100%;
    position: static;
    text-align: left;
    width: 100%;
}
    
    header.subhead {
    display: none;
}
    
    #topNavbar {
    display: none;
}
    
    #globalNavbar {
    display: none;
}
    div.subnav-fixed {
    display: none;
}
    
    body.article .row-contents .span-main .span-main-inr {
    border-left: 1px solid #ccc;
    left: -10px;
    margin: 0 0 0 20px;
    position: relative;
}
}
