@-moz-document domain('tumblr.com') {
/* Adjust Header to be a more reasonable size */
.l-header{
    max-width: 930px !important;
}
.ui_search .search_form_field{
    margin-right: 0;
}

/* Hide empty ad containers */
.yamplus-unit-container{
    display: none;
}

/* Shrink up reblog lists and de-importantify reblogging users' names */
.reblog-list-item{
	padding-top: 10px;
}
.posts .reblog-list+.contributed-content{
	margin-top: 10px;
}
.post .post_content a.reblog-tumblelog-name{
	color: #939292;
	/*font-weight: 500;*/
}
.post .post-source-footer{
	margin-top: 10px;
}

