css:
@-moz-document domain("free-play-mahjong.com") {
#adArea {
	display: none;
}

#mainarea {
	margin-left: 0px;
	width: 100% !important;
}

/* re-center popup dialogs */
#popup-setting, #popup-game, #popup-highscore, #popup-appearance, #popup-options, #popup-about, #popup-help {
	margin-left: 0px !important;
}

/* re-center loading box */
#loading-box {
	margin-left: -100px;
}
}
