css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.roblox.com") /*url-prefix('https://www.roblox.com/users/')*/ {
  #horizontal-tabs {
    display: none;
    visibility: hidden;
  }

  .rbx-tab-content {
    display: table;
    width: 100%;
  }

  #about, #creations {
    display:block;
  }

  #creations {
    display: table-header-group;
  }

  #about {
    display: table;
    width: 100%;
  }

  #about .profile-about {
    display: table-header-group;
  }

  #about #container-list {
    display: table-header-group;
    margin-bottom: 10px;
    margin-top: 10px;
  }

  .game-item-image {
    border-radius: 4px !important;
    box-shadow: 0px 0px 6px 0px rgba(0, 0, 0, 0.49);
    height: 120% !important;
    width: 120% !important;
    margin-left: -10%;
    margin-top: -10%;
  }

  .profile-header-content .header-title, .profile-header-content .header-title h1 {
    text-align: center;
    font-weight: bold;
  }

  .profile-header-content .header-userstatus, .profile-header-content .header-details {
    text-align: center;
  }
}
