css:
@-moz-document domain("simplenote.com") {
.note #txtarea
{
  font-size:15px;
  color:#ddd;
}

.app{
  background:#293134;
}

@media screen and (max-width:1279px){
    .sidebar {
        width:20px
    }

    .sidebar:hover {
        width:360px;
    }

    .sidebar:hover ~ .note {
        left:361px
    }

    .note {
        left:0px   
    }
    ::-webkit-scrollbar {
  		display:none;
	}
}

.wrapper {
  bottom:0px;
}

.footer {
  display:none;   
}

.searchfield
{
  color:#ddd;
}

.notes li {
 padding: 5px;
 text-shadow : none;
}

.notes li .note-preview-title {
 color:#ddd;   
 font-size:15px;
}

.note #txtarea {
  padding:20px;
}

.note #static_content {
    color:#ddd;   
}

.notes li:hover {
    background-color: #555;
}

.notes li.selected {
    background: #555;
}
}
