css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ps3hax.net") {

body, .navtabs, .navtabs li a.navtab, .blockhead,
.forum_info .blockhead, .forum_info .blockbody,
.forumtitle, .forumhead, .foruminfo, .forumrow,
.tabslight dd, #asall, #main, 
.footer, .content, .post, blockquote
{
background-image:none !important;
}
#menu2
{
background-image: url("http://www.thermalzombie.com/stylish/ps3hax.jpg") !important;
background-position: 0px 20px !important; 
background-repeat: no-repeat !important; 
}
#wrapper, .body_wrapper, .navtabs,
.navtabs li a.navtab, .quote_container,
.pagination span a, blockquote
{
background-color:#992516 !important;
}
.postbody, .postrow, .postdetails, .memberprofiletabunder,
.sticky, .nonsticky, .threadstats, .subsection,
.userinfo, #navbar_notice_1, .threadinfo,
.options_input_block, #forum_display_options,
#sidebar, #sidebar li, #sidebar .blockrow, .underblock,
#above_threadlist_controls div, .mainblock, .subsection,
.blockrow, .blockfoot, .mainbox, .foruminfo, .block,
.foruminfo .forumtitle, .forumrow, .forumbit_post div,
.wgo_subblock, .blocksubhead, .section, #as-tabs a,
#moreactivity_container, .tabslight dd, 
.popupbody a:hover, .nonthread, #icon_legends
{
background-color:#333333 !important;
}
.textcontrols, #thread_controls div, .blockhead,
#social_bookmarks_list ul, .forum_info .blockhead,
.forumhead, .forumtitle, .tabs, 
.popupbody, .popupbody a
{
background-color:#111111 !important;
}
input
{
background:#000000 !important;
}
#wrapper
{
border:2px white solid !important;
}
.navtabs, .tabs
{
border-bottom:1px #111111 solid !important;
}
.navtabs li a.navtab
{
border-top:none !important;
}
.navtabs, .forum_info .blockhead
{
box-shadow:none !important;
}
.navtabs li a.navtab
{
text-shadow:none !important;
}
a, p, dd, dt, input, strong, label, span, blockquote, .title,
.navtabs li a.navtab, .usertitle, .postcontent,
#navbar_notice_1, .threadstats, .widget_post_content,
.threadbit, .blockrow, .restore, .forumdescription,
.forumstats, .blocksubhead, h1, h2, h3, h4, h5
{
color:#FFFFFF !important;
}
}
