css:
@-moz-document domain("duckduckgo.com") {
body, #pg-index {
    background-color:transparent !important;
	background-image:url("http://i.imgur.com/XsskRNJ.png") !important;
	background-attachment:fixed !important;
	background-position:left center !important;
	background-repeat:no-repeat !important;
    background-size:100% !important;
}

div.site-wrapper, div#header_wrapper, div.header:after, .header-wrap:after, div.zci, div.zci__metabar-wrap, div.search__autocomplete {
	background:transparent !important;
    border:none !important;
}

div#zero_click_wrapper, div#did_you_mean {
 	border:none !important;
    background:rgba(33,33,33,0.6) !important;
}

div#did_you_mean {
 	text-shadow:1px 0 0 #333333 !important;   
}

a#logo_homepage_link, div.tag-home, a.result__check, span.header__logo {
 	visibility:hidden !important;
}

div#links {
    margin:auto !important;
    background:rgba(33,33,33,0.6) !important;
}

div.tile, form#search_form_homepage {
 	background:rgba(33,33,33,0.6) !important;   
}

div.highlight {
    background:rgba(33,33,33,0.2) !important;
}

.zci:before {
 	display:none !important;   
}

.header-wrap--home, div.site-wrapper-border {
 	border:none !important;
    display:none !important;
}

div#duckbar {
 	background:#333333 !important;   
}

.zcm-wrap--header {
 	margin-left:0px !important;   
}
}
