css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https?://www\\.addic7ed\\.com/serie/.*') {
  
  /* removed all social buttons */
  body center:nth-child(1) table tr:nth-child(4),
  body center:nth-child(1) table tr:nth-child(5) {
    display: none;
  }
  /* OR MORE RADICAL WAY removed all logo and buttons (login, ...) */
  body center:nth-child(1) {
    display: none;
  }
  
  /* removed episode detail panel */
  #container {
    display: none;
  }
  
  /* removed comments section */
  body center:nth-child(2) div:last-of-type {
    display: none;
  }
  
  /* nice top spacing */
  body center:nth-child(2) > table:first-of-type {
    margin-top: 5em !important;
  }
  
  /* removed unnecessary line breaks */
  body center:nth-child(2) > br {
    display: none;
  }
  
}
