css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(forum.treeofsavior.com)
{
  body {
    background-color:black;
  }
  
  h3 {
    color : white!important;
  }
  
  #reply-control {
    background-color:black!important;
  }
  
  .grippie {
    background-color:#333333!important;
  }
  
  .user-menu .notifications li span {
    color:white!important;
  }
  
  div.menu-links-header .fa, div.menu-links-header a {
    color:white!important;
  }
  
  .dropdown-menu {
    background:black!important;
  }
  
  .wmd-button-bar {
    background-color:#222222!important;
  }
  
  .wmd-preview-scroller, .ember-view, .ember-text-area, .wmd-input {
    background-color:black!important;
    color:white!important;
  }
  
  .headerWhiteBG {
    background-color:black;
  }
  
  .map, .map-collapsed {
    background-color:#222;
  }
  
  .avatars, clearfix {
    background-color:#222;
  }
  
  .links {
    background-color:#222;
  }
  
  a, .fancy-title {
    color : white !important;
  }
  
  .create, .fade-out {
    color : #888888 !important;
  }
  
  .mention {
    background-color : #888888 !important;
  }
  
  .badge, badge-notification, clicks {
    color : black !important;
  }
  
  #reply-control .contents .wmd-input, #reply-control .contents .wmd-preview {
    color:white!important;
  }
  
  #reply-control .contents .wmd-preview {
    background-color:black!important;
    color:white!important;
  }
  
  .fa, .fa-reply, p {
    color:#888888 !important;
  }
  
  .ember-view, .d-link, .user-activity-link {
    color:#333333!important;
  }
}
