css:
@-moz-document domain("partycloud.fm") {
/* hide the bottom row of links */
table tbody tr:nth-child(3) {
	display: none;
}

/* re-display anything in the song list that shouldn't be hidden */
#songList_table tbody tr:nth-child(3) {
	display: table-row;
}

/* hide the text blurb above the song list */
.songList_lineStyle2:nth-child(1) {
	display: none;
}

/* hide the demo video */
.folderList_video {
	display: none;
}

}
