css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("birdz.sk") {
  
.container {
    width: 1400px;
    padding: 0 10px;
    margin: 0 auto;
}

.feed .item_caption a {
    font-weight: 700;
    font-size: 20px;
}

.feed .item_caption {
    margin: 0 0 10px;
    color: rgba(123, 123, 123, 0.84);
    font-size: 15px;
    font-weight: 700;
}


.feed .item--status .status-wrapper {
    margin: 0 150px;
}

.feed-card {
    border: 1px solid #d0d4d6;
    margin: 0 150px;
    background: #ecf0f1;
}

.feed .reactions {
    margin: 0;
    font-size: 15px;
}



.feed .comment_content {
    font-size: 23px;
    font-weight: 300;
}

.feed .comment_name {
    margin-right: .18em !important;
    font-size: 22px;
    font-weight: 400;
}

.feed .comment_other {
    display: inline-block;
    color: #797c7d;
    font-size: 14px;
}

/* ---------------------------------------- WEBKA ------------------------------------*/

.birdz.layout-1 .content-main, .bprofile.layout-1 .content-main {
    padding: 10px 10px 0 200px;
}

.status {
    position: relative;
    display: block;
    width: 100%;
    max-width: 1000px;
    font-size: 14px;
    overflow: hidden;
}

.birdz.layout-1 .content-main .sub-nav, .bprofile.layout-1 .content-main .sub-nav {
    margin-left: 0px;
    margin-bottom: 10px;
}

.comment_content {
    margin: 3px 0 0;
    font-size: 22px;
    font-weight: 300;}

}
