css:
@-moz-document domain("nextgenupdate.com") {
/** NGU Global **/

body {
    background: #232f3b url(http://i.imgur.com/1JQgQl4.jpg) repeat-x top center;
    font-family: Arial !important;
}

#nguheader > div.navbar.navbar-default.navbar-static-top {
    background: #004c80 !important;
    position: fixed;
    width: 100%;
    z-index: 100001;
}

#nguheader > div.divide-nav.hidden-xs {
	background: #103b5c !important;
    position: fixed;
    width: 100%;
    margin-top: 40px;
    z-index: 100000;
}

div.body_wrapper {
    padding-top: 90px;
}

div.shoutbox_head {
    background: #024e82 url(http://i.imgur.com/1it2g9k.png) repeat-x !important;
}

#nguheader > div.navbar.navbar-default.navbar-static-top > div > div.navbar-header > a > img {
    -webkit-filter: opacity(0);
    filter: opacity(0%);
}

#nguheader > div.navbar.navbar-default.navbar-static-top > div > div.navbar-header > a {
	background: url(http://i.imgur.com/DPU1of9.png) no-repeat center 2px;
}

.block1 p {
    background: url(http://i.imgur.com/DPU1of9.png) no-repeat scroll left 35px;
    height: 100px;
    -webkit-filter: grayscale(1);
    filter: grayscale(100%);
}

.block1 {
    height: 100px;
}

@media screen and (max-width: 900px) {
    #nguheader > div.navbar.navbar-default.navbar-static-top > div > div.navbar-header > a {
		background: url(http://i.imgur.com/DPU1of9.png) no-repeat center 12px;
        height: 60px;
	}
}

div.forumhome_forum_block_title {
    color: #467ea4;
}

div.forumhome_forum_block_head {
    border-top: 4px solid #467ea4;
}
}

@-moz-document regexp("http://www.nextgenupdate.com/forums/showgroups.php(.*)") {
/** NGU Showgroups **/

.body_wrapper > ol > li.block > h2 {
    background: url(http://i.imgur.com/juQfdAC.png) no-repeat scroll 10px center #f6f7f8 !important;
}
}

@-moz-document regexp("(http://www.nextgenupdate.com/forums/|http://www.nextgenupdate.com/forums/forum.php|http://www.nextgenupdate.com/forums/index.php)") {
/** NGU Forum Index **/
div.forumhome_forum_bit > div.forumhome_forumbit_left > span {
    background-color: #024e82 !important;
}
}
