.ppCBoard-wfield{background: url("http://s15.postimg.org/s69qklx9j/lights.jpg")!important;}

/*main background: change your background by changing this url*/
#arenaMainLayout{background-image: url("http://s24.postimg.org/d20qib6d1/ubw2.jpg")!important;}

.ppPanel-body, .cbPlayerPaneBox, .panel-heading, .panel, .panel-default, .pnael-body,
.ppPanel, .table, .panel-footer
{background: rgba(0,0,0,0.3)!important;}

.dropdown-menu-right, .dropdown-menu{background: rgba(0,0,0,0.7)!important;}
#mtab-index-main,div[id^="mtab-room-"],div[id*="mtab-room-"]{left:70px !important;}


}
