css:
@-moz-document domain("asiae.co.kr") {
  #aside, 
  #header > DIV.main_top > DIV.real_search,
  #content > DIV.news_view_ad,
  #bodyContents > DIV.article > DIV.txt > DIV,
  #content > DIV.ad_view_bt,
  #content > DIV.photo_bx01,
  .racing_gallery
  {
      display: none;
  }

  #content {
      float: none !important;
      margin: 0 auto;
      border: none;
  }

  .article_content, #content {
      font-family: "KoPubBatang", "나눔명조", "맑은 고딕", "나눔바른고딕", "나눔고딕" !important;     
  }
  
  #bodyContents > DIV.article > DIV.txt {
      font-size: 18px;
      line-height: 1.85em !important;
  }

  #container, .view_bg_box {
      background:white !important;
  }
}
