css:
@-moz-document domain("news.donga.com") {
  #aside, 
  #header > DIV.main_top > DIV.real_search,
  #contents > DIV.article_view > DIV.article_txt > DIV.article_relation > DIV.ar_box02,
  #contents > DIV.article_view > DIV.article_txt > DIV.article_relation > DIV.ar_title02,
  #contents > DIV.clickArticleWrap,
  #contents > DIV.trend_news,
  #todayDonga
  {
      display: none;
  }

  #contents {
      float: none !important;
      margin: 0 auto !important;
      border: none !important;;
  }

  .article_content, #content {
      font-family: "KoPubBatang", "나눔명조", "맑은 고딕", "나눔바른고딕", "나눔고딕" !important;     
  }
  
  #bodyContents > DIV.article > DIV.txt {
      font-size: 18px;
      line-height: 1.85em !important;
  }

  #container {
      background:white !important;
  }
}
