css:
@-moz-document url-prefix(http://www.wikipedia), url-prefix(http://en.wikipedia) {

#footer, 
#p-cactions,
#p-interaction,
#p-lang, 
#p-logo,
#p-navigation,
#p-personal,
#p-search h5,
#p-tb,
#protected-icon,
#searchGoButton,
#siteNotice,
#siteSub,
#siteSub *,
.visualClear {
  display: none !important;
}

#p-search {
  border: 1px solid #aaaaaa !important;
    border-bottom: 0 !important;
  margin: 0 !important;
  padding: 0 !important;
  position: absolute !important;
    top: 40px !important;
    right: 40px !important;
  width: 305px !important;
  z-index: 2 !important;
}

#searchBody {
  border: 0 !important;
  margin: 0 !important;
  padding: 20px 5px 0 5px !important;
}

#searchInput {
  font-size: 12px !important;
  width: 150px !important;
}

#mw-searchButton {
  position: relative !important;
    top: -1px;
  width: 50px !important;
}

#searchInput, #mw-searchButton {
  display: inline !important;
}

#content {
  margin: 40px !important;
  margin-top: 82px !important;
  padding: 20px !important;
}

#content,
#bodyContent > H3:first-child + DIV + DIV + DIV + TABLE {
  border: 1px solid #aaaaaa !important;
}

#toc {
  border: 1px solid #aaaaaa !important;
}

#bodyContent > H3:first-child + DIV + DIV + DIV + TABLE,
#bodyContent > H3:first-child + DIV + DIV + DIV + TABLE + TABLE {
  margin: 0 0 0 20px !important;
  position: relative !important;
    top: -20px !important;
}

h1.firstHeading {
  border: 0 !important;
  font-size: 2.5em !important;
  margin-top: 0 !important;
  padding-top: 0 !important;
}

}
