css:
@-moz-document domain("fmylife.com") {
#background {
    background: -webkit-gradient(linear,left top,left bottom,from(#22252A),to(#22252A)) no-repeat #22252A;
    background: -moz-linear-gradient(top,#22252A,#22252A) no-repeat #22252A;
    background-size: 100% 225px;
    -moz-background-size: 100% 225px;
    background-color: #22252A;
}

.post {
    background-color: #2E3135;
     box-shadow: none;
}

.post a.fmllink, .post a.fmllink:hover {
    color: #FFFFFF;
}

#topbar{
    background-color: #3C3C3C;
}

.part{
display:none;
}

#content, .wrapper{
width: 98%;
}

#content{
    margin: 1% 1% 0% 1%;
}

.special{
display:none;
}

#footer{
display:none
}

p{
font-size: 17px;
}

.post .date {
    font-size: 100%;
}

.topcomment p {
    font-size: 15px;
    color: #ffffff;
    margin-bottom: -20px;
        width: 83%;
}

#topbar .menu h1 {
background-color: rgb(46, 49, 53);
    box-shadow: none;
}

#topbar .menu h1 a {
    text-shadow: none;
    text-decoration: none;
    color: #FFFFFF;
}
#topbar .menu h1 .submenu_container .submenu {
    background: #2E3135;
}
.more{
display:none;
}

a, .post a.bf {
color: #4CB59D;
}

.pagination ul li {
    background-color: #22252A;
}
#topbar .menu a, #topbar .menu a:hover {
    color: #3AA89D;
}
#topbar .menu h1 .submenu_container .submenu ul li a:hover {
    background: #22252A !important;
    color: #fff !important;
}
#topbar .menu h1#user {
    background-color: #969696;
}
.topcomment p.right {
    text-align: right;
    width: 100%;
    height: 20px;
    margin: 0px 0px 0px 0px;
}
.topcomment {
    background: rgb(78, 78, 78);
    border-left: 1px solid rgba(0,0,0,0.5);
    padding: 5px 10px;
    margin: 0 5px 15px 25px;
}
.pagination a:hover {
    color: #4CB59D;
    text-decoration: underline;
}
.pagination a {
    color: #FFF;
    text-shadow: none;
    font-weight: normal;
    display: block;
}
.pagination ul li {
    box-shadow: none
}
#topbar #recherche_haut input#mot {
    background: none;
    padding: 1px 8px 2px 8px;
    background-color: white;
}
.post .date .right_part {
    float: left;
    margin-left: 4px;
    border-left: 2px solid #585858;
    padding-left: 5px;
}
body {
    color: #FFFFFF;
        background-color: #22252A;
}
}
