css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("codex.wordpress.org") {
  body {
    background: none repeat scroll 0 0 #444444;
    color: #888888;
  }
  
  code span{
    color: #7CB6CF !important;
  }
  #bodyContent, #bodyContent h2, #bodyContent h3, code, pre, h3{
    color: #aaa;
  }
  
  pre, #toc{
    background-color: #333;
    border-color: #555;
  }
  
  #bodyContent h2, #bodyContent h3, p a, #pagebody .submenu li{
    border-bottom-color: #666;
  }
  
  a{
    color: #4ca6cf;
  }
  
  a:visited{
    color: #7CB6CF;
  }
  
  #headline, #wporg-footer{
    background-color: #345;
  }
  
   #wporg-footer{
     border-top-color: #7cb6cf;
   }
  
  #headline h2,   #headline h2 a, .mw-headline, #bodyContent h2.pagetitle {
    text-shadow: none;
    color: #ccc;
  }
  
  #pagebody table.widefat tbody tr.hover, #pagebody table.widefat tbody tr:hover {
    background-color: #333;
  }
  
  #localNotice div {
    background: none repeat scroll 0 0 #686550 !important;
    border-color: #fcecad !important;
    border-radius: 28px;
    color: #fcecad !important;
  }

}
