css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://blahtherapy.com/") {
  #diff-types {
    background-color: black;
    color: lightgray;
  }
  
  div.pic { /* we don't need those pics */
    display: none;
  }
  
  div.press-home {
    display: none;
  }
  
  #b-people {
    background-color: black;
  }
  
  #who-chat {
    background-color: #222222;
  }
}

@-moz-document url("http://blahtherapy.com/chat-hub/") {
  
  /* All below is from main page. */
  
  div.col-sm-4 { /* Optional - to disable right column with twitter and this kind of shit. */
    display: none;
  }
  
  body {
    background-color: black;
  }
  
  div.headbox {
    background-color: black;
  }
  
  div.post-578 style {
    display: none;
  }
  
  div.cbox {
    background-color: black;
  }
  
  div.page {
    background-color: black;
  }
  
  div.blog-page {
    display: none;
  }
  
  div.col-md-8 {
    visibility: hidden;
  }
  
  div.col-md-8 div {
    visibility: visible;
  }
  
  div.sidebar-title {
    color: green;
  }
  
  div.row {
    background-color: black;
  }
  
  .cbox{ /* Can somebody make it CENTERED?! */
    border-top: 0px dashed #ffffff;
  }
  
  /* ALL BELOW IS FOR CHAT PAGE */
  
  div.chatmsgwrapper {
    background-color: darkgray;
    color: lightgrey;
  }
  
  div.connect { /* Doesn't work anyway. */
    background-color: black;
    color: lightgrey;
    display: none;
  }
  
  #details { /* Why the fuck would we need that for anyways? Alias is everywhere already. */
    display: none;
  }
  
  div.logwrapper {
    background-color: black;
  }
  
  div.logbox {
    color: lightgray;
  }
  
  a.button {
    background-color: darkgray;
    color: blue;
  }
  
  span.nick_name::after {
    color: lightgray;
    text-shadow: none;
  }
  
  strong {
    text-shadow: none;
  }
}
