css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hackaday.com") {

  .site-header,
  #secondary,
  .featured-slides,
  .slick-initialized,
  .slick-slider,
  #footer,
  #leaderboard,
  .amr_widget{
    display: none;
  }
  
  #content,
  #primary,
  #main {
    padding: 5px !important;
    margin: 0 !important;
    width: 100% !important;
    color: #A0A0A0;
  }
  
  #page {
    color: white;
    background: black;
  }
}
