css:
@-moz-document domain("youtube.com") {
:focus{outline: none!important}
button::-moz-focus-inner{border: 0!important}
html,img,video,input,select,iframe{
-webkit-filter: invert(100%) hue-rotate(180deg)!important;
}
input,body{
  background-color: #000000 !important;
  color:#fff !important;
}

#theater-background,.html5-video-player{
  background-color: #fff !important;
}
#movie_player.ytp-fullscreen,
#movie_player.endscreen-loaded .html5-endscreen,
.ytp-chrome-bottom,
.ytp-gradient-bottom,
#watch-appbar-playlist{
  -webkit-filter: invert(100%) hue-rotate(180deg)!important;
}
button{
  -webkit-filter:hue-rotate(240deg)!important;
}
#watch-appbar-playlist img{
  -webkit-filter: none !important;
}
#yt-dialog-bg,
#alerts,
#ticker{
    display: none!important
}
.yt-dialog-bg, 
.yt-uix-overlay-bg{
  z-index:1!important;
}
#masthead-search-terms{background-color:#000 !important;}
.player-api{background-color:white!important;}
}
