css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("asriran.com") {
img.img-responsive[src*="adv"],
object[data*="adv"] {
	display: none!important;
	width: 0!important;
}
#home .col-md-3 {
	width: 25%!important;
}
#home .container .row .col-md-7 {
	width: 75%!important;
}
/*fancy little css3 animation*/

#news img.img-responsive {
	border-radius: 5px!important;
	-moz-border-radius: 5px!important;
	-webkit-border-radius: 5px!important;
	box-shadow: 2px 1.5px 6px 2px #000;
	-moz-box-shadow: 2px 1.5px 6px 2px #000;
	-webkit-box-shadow: 2px 1.5px 6px 2px #000;
	transform: scale(0.99);
	-webkit-transform: scale(0.99);
	-moz-transform: scale(0.99);
	transition: all 0.5s ease-in-out;
	-moz-transition: all 0.5s ease-in-out;
	-webkit-transition: all 0.5s ease-in-out;
	transition-delay: 0.8s;
}
#news .col-sm-4.padd_left {
	opacity: 0.1;
	filter: alpha(opacity=10);
	transition: opacity 0.5s ease-out;
	-moz-transition: opacity 0.5s ease-out;
	-webkit-transition: opacity 0.5s ease-out;
}
#news .col-sm-4.padd_left:hover {
	opacity: 1.0;
	filter: alpha(opacity=100);
	transition: opacity 0.4s ease-in;
	-moz-transition: opacity 0.4s ease-in;
	-webkit-transition: opacity 0.4s ease-in;
}
input[class="form-control"]:focus,
textarea[class="form-control"]:focus {
	box-shadow: 1px 1.5px 6px 2px #86DEF9!important;
	-moz-box-shadow: 1px 1.5px 6px 2px #86DEF9!important;
	-webkit-box-shadow: 1px 1.5px 6px 2px #86DEF9!important;
}
}
