css:
@-moz-document domain("storify.com") {
.Tweet-brand,
.Tweet-authorName,
.Tweet-authorScreenName,
.s-clear,
.s-element-options,
.lf-custom-thread-btn,
.lf-thread-btn {
    display: none;
}
.Tweet-authorAvatar {
    left: -1.5rem;
    transform: translate(-50px, 0);
}
.s-element-container,
.EmbeddedTweet,
iframe {
    max-width: none !important;
}
.EmbeddedTweet {
    overflow: visible;
    margin-left: 50px;
    border: none !important;
}
.Tweet-header.u-cf,
.s-element {
    margin: 0 !important;
}
.EmbeddedTweet .Tweet-body >:not(.Tweet-text), #related-stories {
    display: none !important;
}
.Tweet-text:after {
    content: "\a";
    white-space: pre;
}
.EmbeddedTweet-tweet {
    padding-top: 0;
    padding-bottom: 0;
}
#cover.custom {
    height: auto;
    max-height: none;
    min-height: 0;
}
#cover.custom .info {
    position: static;
}
}
