css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("asana.com") {
    .asanaView-paneGutter {
        display: none;
    }

    .asanaView-body {
        padding-top: 0;   
    }
    
    #navigation_dock {
        width: 215px;
    }
    
    #right_pane_container {
        margin-left: 0;
    }
    
    .grid_cell_item_number {
        width: 7px;
    }
    
    .grid-uncompact .grid_cell_show_details {
        padding-right: 9px;
        width: 0;
    }
    
    .details-pane-title-row {
        margin: 0;
    }
    
    .taskDetailsView-description {
        margin: 0;
        padding: 5px;
    }
}
