css:
@-moz-document url-prefix("http://www.boligportal.dk/lejebolig/dp") {
/* Better subheader style */
p.city {
	font-size: 18px;
	color: #928A86;
	margin-top: 4px;
	display: block;
}

@media (max-width: 650px) {
	/* Make keymetrics look better on mobile */
	.styleguide .keymetrics .metric {
		margin: 0;
		width: 25%;
	}
	
	.styleguide .keymetrics .key_desc,
	.styleguide .keymetrics .key_value {
		display: block;
		width: auto;
		text-align: center;
		padding-left: 0;
	}
	
	.styleguide .keymetrics .key_desc {
		color: #928A86;
	}
	
	/* Bigger mobile CTA button */
	.styleguide .mobile-only .cta-buy {
		padding: .8em;
	}
}

@media (max-width: 400px) { 
	/* Smaller icons on Mobile */
	.styleguide .keymetrics .icon img {
		height: 32px;
	}
}
}
