css:
@-moz-document domain("www.khabaronline.ir") {
/*disable ads*/

.leftCol .advCon img[src*="menus"],
.headerBannerPlaceHolder .advCon img[src*="menus"],
.topNewsAdvCont .advCon img[src*="menus"],
#sabavisionbody23639,
.advCol {
	display: none!important;
}
.homeMainContainer>.rightCol,
.detailPageCont .rightCol,
.serviceMainContainer>.rightCol {
	width: 73%!important;
}
/*.servicePosition2Container, .servicePosition2Container ul {width: 100%;}
.servicePosition2Container .newsItem{width: 50%!important;}*/

input[id="Name"]:focus,
input[id="Email"]:focus,
input[id="WebSite"]:focus,
textarea[id="CommentText"]:focus {
	box-shadow: 1.5px 1px 10px 2px #BDBDBD !important;
	-moz-box-shadow: 1.5px 1px 10px 2px #BDBDBD !important;
	-webkit-box-shadow: 1.5px 1px 10px 2px #BDBDBD !important;
	-webkit-transition: box-shadow 0.2s ease-out;
	-moz-transition: box-shadow 0.2s ease-out;
	transition: box-shadow 0.2s ease-out;
}
.lblMsg {
	font-size: 1.2em!important;
}
.newsBodyCont img:hover {
	box-shadow: 2.5px 2.5px 10px 3px #000 !important;
	-webkit-transition: box-shadow 0.5s ease-in-out;
	-moz-transition: box-shadow 0.5s ease-in-out;
	transition: box-shadow 0.5s ease-in-out;
}
.newsBodyCont img {
	box-shadow: none!important;
	-webkit-transition: box-shadow 0.5s ease-in-out 1s;
	-moz-transition: box-shadow 0.5s ease-in-out 1s;
	transition: box-shadow 0.5s ease-in-out 1s;
}
}
