css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("movie-blog.org") {

div#page, div#title {
  width: calc(900px + 300px + 165px);
    
}    

    
div#archiv {
  width: calc(560px + 300px);
  background-color: #fff;
  border-radius: 25px; 
  padding-left: 45px;
  padding-right: 35px;
  margin-left: 210px;
}  
    
div.post-date, div#archiv> div.post> div.eintrag_x, div#archiv> div.post> p.info_x, div.beitrag> b {
  display: none !important;
}    
  
div#dp_layer {
  position: fixed !important;
  margin: 0px;
  left: -2000px;
}  
  
html > div {
  display: none;
}  
    
div#content {
  width: calc(560px + 300px);
  background-color: #fff;
  border-radius: 25px;
  margin-left: 215px;
}    

center> a> img{
  border-bottom-left-radius: 25px;
  border-bottom-right-radius: 25px;
}    
    
div#sidebar {
  padding-right: 15px;
  padding-left: 15px;
  padding-top: 15px;
  background-color: #fff;
  border-radius: 25px;

}
 /*  
.eintrag2 img {
  float: right;
  width: calc(297px/2);
  height: calc(436px/2);
}    
   */
h1.beitrag2, span.fn, .beitrag2> h1 a {
  font-size: 16px;
  color: black !important;
  font-weight: bold; 
}    
    
iframe {
  display: none;
}    

/* blendet fast alles auf page aus */
div#page > div#archiv > div.beitrag4 > p.info_x,
div#page > div#archiv > div.beitrag4 > div.eintrag_x > .wprp_wrapper,
div#page > div#archiv > div.beitrag4 > div.eintrag_x > p:nth-child(2),
div#page > div#archiv > div.beitrag4 > div.eintrag_x > blockquote,
div#page > div#archiv > div.beitrag4 > div.eintrag_x > .post-ratings,
div#page > div#archiv > div.beitrag4 > div.eintrag_x > p > a > img,
div#page > div#content > div.beitrag4 > div.eintrag2 > p:first-child,
div#page > div#content > div.beitrag4 > div.eintrag2 > blockquote,  
div#page > div#content > div.beitrag4 > div.eintrag2 > .wprp_wrapper,
div#page > div#content > div.beitrag4 > div.eintrag2 > div.sp-wrap.sp-wrap-default,
div#page > div#content > div.beitrag4 > table {
    display: none;
}
  


/* page anders angeordnet */
div#page > div#content > div.beitrag2 > p.date,
div#page > div#archiv > div.beitrag2 > p.date_x,
div#page > div#archiv > div.post > p.date_x,
div#page > div#archiv > h2.archivtitle,
div#page > div#archiv > div.beitrag2 > div.eintrag_x {
    margin-left: 0px !important;
}
    
div#page > div#content > div.beitrag4 > div.eintrag2  img,
div#page > div#archiv > div.beitrag4 > div.eintrag_x img{
  float: left;
  border-style: none !important;
  max-width: 200px !important;
  max-height: 305px !important;
}
    
div#page > div#content > div.beitrag4 > div.eintrag2 {
    margin-top: 0px;
   /* min-height: 320px;/*300*/
}

  div#page > div#content > div.beitrag4 > div.eintrag2 {
    overflow: auto;
  } 
/* folgende grafik im werbeblocker blockieren: 
/* http://www.movie-blog.org/wp-content/themes/concrete/images/back.jpg */
}
