css:
@-moz-document domain("client.wedos.com") {
div.list_div {
    width: auto!important;
    border: none;
}

table.list_header,
div.list_data {
    width: 100%!important;
	height: auto!important;
}

div.list_data table tr > td:nth-of-type(1) {
    min-width: 35px!important;
    text-align: center!important;
}

#frm1 fieldset {
    width: 100%!important;
}

div.list_data table tr > td:nth-of-type(2) { min-width: 155px; }
div.list_data table tr > td:nth-of-type(3) { min-width: 45px; }
div.list_data table tr > td:nth-of-type(4) { min-width: 50px; }

}
