css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.onlinetvrecorder.com") {

    
    /* Vorschautexte sichtbar machen, wenn Maus darüber fährt */
    .homethree:hover {
       overflow: visible !important;
    }
    
    .homethree:hover a {
        z-index: 3 !important;
    }
  
    .homethree:hover .homethreea {
       overflow: visible !important;
       z-index: 2 !important;
     }

    .homethree:hover .homethreec {
       background-color: white !important;
     }


}
