css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anime-planet.com") {

  .pure-table-striped tr:nth-child(2n) td
{
  background-color: #242424;
}
  .pure-table-striped tr:nth-child(2n-1) td
    {
  background-color: #060606;
}
  #siteContainer
  {
  background-color: #060606;
}
  .pure-table td, .pure-table th
  {
  background-color: #060606;
}
  #siteHeader
  {
  background-image: none;
}
  #siteFooter
    {
  background-color: #060606;
}
  #siteHeader.collapse
  {
  background-color: #060606;
}
  #siteFooter > *
  {
  background-color: #060606;
}
  /* Background */
       body {background: none repeat scroll 0% 0% #303030; 
             background-image: url('https://ellargoviajeacasa.files.wordpress.com/2014/03/black-background.jpg') !important;
             background-attachment: fixed;
             background-position: center; 
             }
.bg-themeColor
    {
  background-color: #121212;
}
  .cta
   {
  background-color: #242424;
}
html, button, input, select, textarea, [class*="pure-g"] [class*="pure-u"]
  {
color: #7b7b7b;}
  #tooltipModal 
    {
  background-color: #060606;
}
.ui-dialog .ui-dialog-title
  {
  background-color: #060606;
}
  .ui-dialog
  {
  background-color: #060606;
}
 .ui-draggable .ui-dialog-titlebar
    {
  background-color: #060606;
}
 #siteNav > ul > li ul
    {
  background-color: #242424;
}
  a:hover, .a:hover
    {
    color: #d9d9d9;
      }
    #siteSearch .dropSearch
   {
  background-color: #242424;
}
  input, select, textarea

  {
  background-color: #242424;
}
  .loggedInOptions
 {
  background-color: #242424;
}
  .profLoa
  {
  background-color: #060606;
}
  .ui-tooltip-content
 {
  background-color: #242424;
}
  .ui-dialog .ui-dialog-content
   {
  background-color: #060606;
}
  
}
