css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neogaf.com") {
  
  #usercptools_menu:not([style="display: none;"]) {
    display: -moz-box;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -moz-box-orient: vertical;
    -webkit-box-orient: vertical;
    -webkit-flex-flow: column;
    -ms-flex-direction: column;
    flex-flow: column;
  }
  
  #usercptools_menu li:nth-child(3) {
	 -moz-box-ordinal-group: 1;
	 -webkit-box-ordinal-group: 1;
	 -webkit-order: 1;
	 -ms-flex-order: 1;
	 order: 1;
  }
  
    #usercptools_menu li:nth-child(9) {
  	-moz-box-ordinal-group: 2;
 	  -webkit-box-ordinal-group: 2;
  	-webkit-order: 2;
  	-ms-flex-order: 2;
  	order: 2;
  }
  
}
