css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("quill-new.rev.com") {

.page {
  background: #404141;
}

body {
	color:#CECECE;
}

div.subtitles .end{
  color: #FFF;
}

div.subtitles ul.subtitle-list li textarea, div.subtitles ul.subtitle-list li span.subtitle-text {
	border-color: #535353 -moz-use-text-color -moz-use-text-color;
}

div.subtitles ul.subtitle-list li {
	color: #CECECE;
}

div.subtitles ul.subtitle-list li span.timing {
	color: #CECECE;
}

div.workspace-tools {
	background: #353535 none repeat scroll 0% 0%;
	border-top: 0px solid #DADBDC;
	box-shadow: 0px 0px 0px #E0E1E2 inset, 0px 0px 0px rgba(0, 0, 0, 0.08);
}

div.help-panel {
	color: #9E9E9E;
}

div.key-pattern {
	border-bottom: 1px solid #474849;
	box-shadow: 0px 0px 0px #E0E1E2 inset, 0px 0px 0px rgba(0, 0, 0, 0.08);
	background: #484848 none repeat scroll 0% 0% padding-box;
}

div.instruction-item {
	background: #333 none repeat scroll 0% 0%;
}

div.workflow li {
	border-bottom: 1px solid transparent;
}

div.workflow li.active {
	color: #CECECE;
}

div.workflow li.active h3 {
	color: #CECECE
}

div.notes .new-note textarea {
	background: #3E3E3E none repeat scroll 0% 0%;
	color: #CECECE;
}

div.workflow .save-status {
	background-color: #272727;
	color: #CECECE;
}

div.subtitles-wrapper div.info-tray {
	border-right: 1px solid #565758;
	border-left: 1px solid #565758;
	border-top: 1px solid #565758;
	border-bottom: 1px solid #565758;
	background: rgb(64, 65, 65) none repeat scroll 0% 0%;
}

div.timeline div.subtitle {
	background: #474747 none repeat scroll 0% 0% padding-box;
}

div.subtitles ul.subtitle-list li.current-subtitle {
	background: #545454 none repeat scroll 0% 0%;
}

div.timeline div.subtitles-container div.unsynced {
	background: #666 none repeat scroll 0% 0%;
}

div.workflow .action {
	color: #CECECE
}

div.help-panel div.instruction-item {
	background: #575757 none repeat scroll 0% 0% !important
}


}
