/**
 * @name Stylish Editor Slim
 * @stylish-id 119669
 * @author Niklas Gollenstede
 * @licence CC-BY-SA-4.0 or MIT
 * @description
 *     Makes the Firefox Stylish editor Ui-bar smaller.
 *
 *     If you have any issues with this style, please open a ticket at https://github.com/NiklasGollenstede/stylish
 */

@-moz-document
    url-prefix('about:stylish-edit')
{

    #editor-tools + separator
    {
        display: none;
    }

    #editor-tools
    {
        margin-left: 400px;
    }

    #editor-tools + separator + grid
    {
        position: fixed;
        top: 5px;
        left: 0px;
        width: 400px;
    }
    grid rows
    {
        width: 100%
    }

}

@-moz-document
    url-prefix(data:text/html;charset=utf8)
{

    body.devtools-monospace>.CodeMirror
    {
        font-size: 16px;
    }

}
